export default function sort(table, fields, args) {
    table.data.sort((a,b) => compareRow(a,b, fields));
    table.summarize();
}

function compareRow(row1, row2, fields, fieldTypes){
    for (let f of fields){
        if (row1[f] < row2[f])
            return -1;
        else if (row1[f] > row2[f])
            return 1;
    }
    return 0;
}
