import Group from "./Group";
import {ItemType} from "../../util/Constants";
import {ItemCounter} from "../../util/ItemUtil";

export default class Glyph extends Group {
	
	constructor(args) {
		super();
		this.type = ItemType.Glyph;
		this._id = this.type + ItemCounter[this.type]++;
		if (args){
			for (let a of args){
				this.addChild(a);
			}
		}
	}

	duplicate() {
		let g = this.getScene().glyph();
		for (let c of this.children){
			g.addChild(c.duplicate());
		}

		g.classId = this.classId;
		if (this._dataScope) {
			g.dataScope = this._dataScope.clone();
		}
		return g;
	}
}